import { useState, useEffect, useRef } from 'react';
import './App.css';
import MessageList from './MessageList';
import MessageForm from './MessageForm';
import Login from './Login';

function App() {
  const [messages, setMessages] = useState([]);
  const loginVisible = useRef<boolean>(true);

  useEffect(() => {
    deleteMessages();
    retrieveMessages();
  }, []);

  async function retrieveMessages() {
    const response = await fetch("http://127.0.0.1:5000/retrieve");
    const data = await response.json();
    setMessages(data.store);
  }

  async function deleteMessages() {
    const options = {
      method: "DELETE",
    }

    const response = await fetch("http://127.0.0.1:5000/deleteAll", options);
    const data = await response.json();
    console.log(data);
  }
  
  return (
    <div className="App">
    {!loginVisible.current &&
      <div >
        <MessageList messages={messages} />
        <MessageForm />
      </div>
    }
    {loginVisible.current &&
      <div>
        <input type="text" placeholder="First Name"></input>
        <input type="text" placeholder="Last Name"></input>
        <input type="text" placeholder="Email"></input>
        <button onClick={() => loginVisible.current = false}>Login</button>
      </div>
    }
    </div>
  );
}

export default App;
