import { useState, useEffect, useRef } from 'react';
import './App.css';
import MessageList from './MessageList';
import MessageForm from './MessageForm';

function App() {
  const [messages, setMessages] = useState([]);
  const deleteAtStart = useRef<boolean>(true);

  useEffect(() => {
    deleteMessages();
    retrieveMessages();
  }, []);

  useEffect(() => {
    if (!deleteAtStart) return;

    deleteMessages();
    deleteAtStart.current = false;
  }, []);

  async function retrieveMessages() {
    const response = await fetch("http://127.0.0.1:5000/retrieve");
    const data = await response.json();
    setMessages(data.store);
  }

  async function deleteMessages() {
    const options = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify([]),
    }

    const messages = await fetch("http://127.0.0.1:5000/deleteAll", options);
  }
  
  return (
    <div className="App">
      <MessageList messages={messages} />
      <MessageForm />
    </div>
  );
}

export default App;
